import './index.css';
import {useState} from "react";

function App() {

    // eslint-disable-next-line no-undef
    const [todos, setTodos] = useState([
        {
            text: 'купить Бананы',
            favorite: false
        },
        {
            text: 'купить огурец',
            favorite: true
        },
        {
            text: 'купить дыню',
            favorite: true
        },
    ]);

    const makeFavorite = (i) =>{
        const newTodos = todos.map((item, index) =>{
            if(index === i){
                return {
                    ...item,
                    favorite: !item.favorite
                }
            }
            return item;
        });

        setTodos(newTodos);
    }

    const deletedTodos = (i) =>{
        const filtered = todos.filter((todo, index) => {
            if(index === i){
                return false;
            }
            return true;
        });
        setTodos(filtered);
    }


    const newTodos = todos.map((todo, index) => {
        return(
            <div className={`todo ${todo.favorite ? "selected" : ''}`}>
                <div className="favorite">
                    <button onClick={() => makeFavorite(index)}>★</button>
                </div>
                <div className="todo-text">
                    {todo.text}
                </div>
                <div className="actions">
                    <button onClick={() => deletedTodos(index)}>✖</button>
                </div>
            </div>
        )
    })


  return (
      <div className="app">
        <div className="header">
          Список дел
        </div>
        <div className="form">
          <input placeholder='Ведите текст' type="text"/>
          <button onClick={(e) => addTodos()}>Добавить</button>
        </div>

        <div className="todos">
            {newTodos}
        </div>
      </div>
  );
}

export default App;
