import './index.css';
import {useState} from "react";
import Header from "./Header";
import Form from "./Form";

function App() {

    // eslint-disable-next-line no-undef
    const [todos, setTodos] = useState([
        {
            text: 'купить Бананы',
            favorite: false
        },
        {
            text: 'купить огурец',
            favorite: true
        },
        {
            text: 'купить дыню',
            favorite: true
        },
    ]);

    const [text, setText] = useState('')

    const makeFavorite = (i) =>{
        const newTodos = todos.map((item, index) =>{
            if(index === i){
                return {
                    ...item,
                    favorite: !item.favorite
                }
            }
            return item;
        });

        setTodos(newTodos);
    }

    const deletedTodos = (i) =>{
        const filtered = todos.filter((todo, index) => {
            if(index === i){
                return false;
            }
            return true;
        });
        setTodos(filtered);
    }


    const newTodos = todos.map((todo, index) => {
        return(
            <div className={`todo ${todo.favorite ? "selected" : ''}`}>
                <div className="favorite">
                    <button onClick={() => makeFavorite(index)}>★</button>
                </div>
                <div className="todo-text">
                    {todo.text}
                </div>
                <div className="actions">
                    <button onClick={() => deletedTodos(index)}>✖</button>
                </div>
            </div>
        )
    });

    const addTodo = () =>{
        setTodos([{
            text: text},
            ...todos
        ]);
        setText('')
    }


  return (
      <div className="app">
        <Header />
        <Form text={text} addTodo={addTodo}/>
        <div className="todos">
            {newTodos}
        </div>
      </div>
  );
}

export default App;
